parameters.jsCode:
return items.map(item => {
  const choice = item.json?.choices?.[0];

  const message = choice?.message?.content || "No message content found";

  return {
    json: {
      index: choice?.index ?? null,
      role: choice?.message?.role ?? null,
      finish_reason: choice?.finish_reason ?? null,
      content: message,
    },
  };
});

return items.map(item => {
  const choice = item.json?.choices?.[0];

  const message = choice?.message?.content || "No message content found";

  return {
    json: {
      index: choice?.index ?? null,
      role: choice?.message?.role ?? null,
      finish_reason: choice?.finish_reason ?? null,
      content: message,
    },
  };
});

const combinedContent = items
  .map(item => item.json.content)
  .join('\n\n');


return [{
  json: {
    combinedContent
  }
}];
